import React, {Component} from 'react';
import { Link } from "react-router-dom";

import { Jumbotron, Container, Button } from 'react-bootstrap';

export default class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name:'',
            email:''
        }
    }

    componentDidMount() {

    }

    render() {
    return (
        <div style={{
            backgroundImage: 'url("https://example.com/background-image.jpg")',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            minHeight: '100vh',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            flexDirection: 'column',
            padding: '20px'
        }}>
            <Container style={{ textAlign: "center", maxWidth: "600px" }}>
                <img src="https://example.com/logo.png" alt="SynthiLancer Logo" style={{ width: "200px", marginBottom: "20px" }} />
                <h1 style={{ fontFamily: "Arial", fontSize: "3rem", color: "#333" }}>Welcome to SynthiLancer</h1>
                <p style={{ fontStyle: "italic", color: "#666", marginBottom: "20px" }}>
                    Hayalinizdeki işi bulmanın en kolay yolu!
                </p>
                <Link to="/login">
                    <Button 
                        renderAs="button"
                        style={{
                            width: "150px",
                            borderRadius: "3px",
                            letterSpacing: "1.5px",
                            marginTop: "1rem",
                            backgroundColor: "#3498db",
                            color: "white",
                            boxShadow: "none",
                            border: "none",
                            fontWeight: "bold",
                            fontSize: "1.1rem"
                        }}
                    >
                        <span>Giriş Yap</span>
                    </Button>
                </Link>
                <hr style={{ margin: "40px 0", borderColor: "#ccc" }} />
                <h6 style={{ color: "#888", fontSize: "1.1rem" }}>
                    Hesabınız yok mu? <Link to="/register" style={{ color: "#3498db", textDecoration: "none" }}>Kayıt Olun</Link>
                </h6>
            </Container>
        </div>
    )
}


}
